import React from "react";
import { Link, useParams } from 'react-router-dom'

const ProjectItem = ({ item }) => {
    let link = `/project/${item.id}`
    return(
        <tr>
        <td>{item.id}</td>
		<td>{item.project_name}</td>
		<td><Link to={link}>{link}</Link></td>
		<td>{String(item.users)}</td>
		</tr>
    )
}

const ProjectList = ({ items }) => {
    return(
		<table style={{'margin': 'auto'}}>
		    <th style={{'width': '10%'}}>ID</th>
		    <th style={{'width': '25%'}}>Project</th>
		    <th style={{'width': '55%'}}>Repository link</th>
            <th style={{'width': '10%'}}>Users</th>
		    {items.map((item) => <ProjectItem item={item}/>)}
		</table>
	)
}

const ProjectUserItem = ({item}) => {
    return (
        <li>
            {item.username} ({item.email})
        </li>
    )
}

const ProjectDetail = ({getProject, item}) => {
    let { id } = useParams();
    getProject(id)
    let users = item.users ? item.users : []
    console.log(id)

    return (
        <div>
            <h1>{item.name}</h1>
            Repository: <a href={item.repository}>{item.repository}</a>
            <p></p>
            Users:
            <ol>
            {users.map((user) => <ProjectUserItem item={user} />)}
            </ol>
        </div>
    )
}

export { ProjectList, ProjectDetail }
