import './App.css';
import React from 'react';
import axios from "axios";
import {
    BrowserRouter as Router,
    Route,
    Routes
} from 'react-router-dom'

import UserList from "./components/User";
import ToDoList from "./components/ToDo";
import ProjectList from "./components/Projects"
import Navbar from "./components/Menu";
import LoginForm from "./components/Auth";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      'navbar' : [
        {name : 'Список пользователей', href: '/users'},
        {name: 'Список проектов', href: '/projects'},
        {name: 'Список ToDo', href: '/notes'},
        {name: 'Войти', href: '/login'},
      ],
      'notes': [],
      'users': [],
      'projects': [],
      'token': [],
    };
  }


  componentDidMount() {
    axios.get('http://127.0.0.1:8000/api/notes/')
      .then(response =>{
      this.setState({
        'notes':response.data.results
      })
    }).catch(error => console.log(error));

    axios.get('http://127.0.0.1:8000/api/projects/')
      .then(response =>{
      this.setState({
        'projects':response.data.results
      })
    }).catch(error => console.log(error));

    axios.get('http://127.0.0.1:8000/api/users/')
      .then(response =>{
      this.setState({
        'users':response.data
      })
    }).catch(error => console.log(error));
  }

  get_token(login, password) {
        const data = {username: login, password: password}
        axios.post('http://127.0.0.1:8000/api-token-auth/', data).then(response => {
            console.log(response.data)
        }).catch(error => alert('Неверный логин или пароль'))
    }


  render() {
    return (
      <Router>
        <header>
          <Navbar navbarItems={this.state.navbar}/>
        </header>
        <main role="main" className="flex-shrink-0">
        <div className="container">
          <Routes>
            <Route exact path = '/users' element = {<UserList items={this.state.users} /> } />

            <Route exact path = '/notes' element = {<ToDoList items={this.state.notes} /> } />

            <Route exact path = '/projects' element = {<ProjectList items={this.state.projects} /> } />

            <Route exact path='/login' element={<LoginForm
                get_token={(username, password) => this.get_token(username, password)}/>}/>
          </Routes>
        </div>
        </main>
      </Router>
    )
  }
}


export default App;
